import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/api';
import '../styles/MovieDetails.css';

const MovieDetails = () => {
  const { type, id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = type === 'movie' 
          ? await api.movies.getById(id)
          : await api.tvshows.getById(id);
        setItem(response.data);
      } catch (error) {
        console.error('Error fetching details:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchItem();
  }, [type, id]);

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="movie-details">
      <div className="details-container">
        <div className="poster-container">
          <img src={item.largePoster} alt={item.title} />
        </div>
        <div className="info-container">
          <h1>{item.title}</h1>
          <p className="synopsis">{item.synopsis}</p>
          <div className="pricing">
            <p className="rent-price">Rent: {item.rentPrice}</p>
            <p className="purchase-price">Buy: {item.purchasePrice}</p>
          </div>
          <div className="buttons">
            <button className="play-btn">Play</button>
            <button className="rent-btn">Rent</button>
            <button className="buy-btn">Buy</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetails;
