import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import MovieCard from "../components/MovieCard";
import { Link } from "react-router-dom";
import "../styles/Movies.css";

const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [featuredCategories, setFeaturedCategories] = useState([]);
  const [heroCarousels, setHeroCarousels] = useState([]);
  const heroRefs = useRef([]);

  const allMoviesRef = useRef(null);
  const languageRefs = useRef({});
  const categoryRefs = useRef({});

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);
        const res = await axios.get("http://localhost:8080/api/movies");
        setMovies(res.data || []);

        if (res.data && res.data.length > 0) {
          createFeaturedCategories(res.data);
          createHeroCarousels(res.data);
        }
      } catch (err) {
        setError("Failed to fetch movies.");
        console.error("Error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  const createHeroCarousels = (movies) => {
    const carousels = [
      {
        title: "Black & White Cinema",
        subtitle: "Classic Masterpieces Collection",
        movies: getRandomMovies(movies, 5),
        cta: "Explore Collection"
      },
      {
        title: "Premium 4K Movies",
        subtitle: "Experience Ultra HD Quality",
        movies: getRandomMovies(movies, 5),
        cta: "View All"
      },
      {
        title: "Critics Choice",
        subtitle: "Award-Winning Films",
        movies: getRandomMovies(movies, 5),
        cta: "Browse Awards"
      }
    ];
    setHeroCarousels(carousels);
  };

  const createFeaturedCategories = (movies) => {
    const categories = [
      { title: "Trending Now", key: "trending" },
      { title: "Popular on StreamGo", key: "popular" },
      { title: "Staff Picks", key: "staffPicks" }
    ];

    const featured = categories.map(category => {
      const shuffled = [...movies].sort(() => 0.5 - Math.random());
      return {
        ...category,
        movies: shuffled.slice(0, 9)
      };
    });

    setFeaturedCategories(featured);
  };

  const getRandomMovies = (movies, count) => {
    return [...movies].sort(() => 0.5 - Math.random()).slice(0, count);
  };

  const scroll = (ref, direction) => {
    if (ref.current) {
      const scrollAmount = direction === "left" ? -800 : 800;
      ref.current.scrollLeft += scrollAmount;
    }
  };

  const groupByLanguage = (items) => {
    return items.reduce((acc, item) => {
      const language = item.language || "Unknown";
      if (!acc[language]) {
        acc[language] = [];
      }
      acc[language].push(item);
      return acc;
    }, {});
  };

  if (loading) return <div className="loading-spinner"></div>;
  if (error) return <p className="error">{error}</p>;

  const moviesByLanguage = groupByLanguage(movies);

  return (
    <div className="movies-page">
      {/* Big Hero Carousels */}
      {heroCarousels.map((carousel, index) => (
        <section key={index} className="hero-carousel">
          <div className="carousel-header">
            <h2>{carousel.title}</h2>
            <p className="carousel-subtitle">{carousel.subtitle}</p>
            <Link to="/collection" className="cta-button">
              {carousel.cta}
            </Link>
          </div>

          <div className="carousel-container">
            <button 
              className="scroll-arrow left" 
              onClick={() => scroll(heroRefs.current[index], "left")}
            >
              &lt;
            </button>

            <div 
              className="hero-carousel-items" 
              ref={el => heroRefs.current[index] = el}
            >
              {carousel.movies.map(movie => (
                <Link 
                  to={`/movie/${movie.id}`} 
                  key={movie.id} 
                  className="hero-movie-card"
                >
                  <img 
                    src={movie.largePoster} 
                    alt={movie.title}
                    className="hero-poster"
                  />
                  <div className="hero-card-overlay">
                    <h3>{movie.title}</h3>
                    <p>{movie.year}</p>
                  </div>
                </Link>
              ))}
            </div>

            <button 
              className="scroll-arrow right" 
              onClick={() => scroll(heroRefs.current[index], "right")}
            >
              &gt;
            </button>
          </div>
        </section>
      ))}

      {/* Featured Categories */}
      {featuredCategories.map((category) => {
        if (!categoryRefs.current[category.key]) {
          categoryRefs.current[category.key] = React.createRef();
        }

        return (
          <section key={category.key} className="featured-section">
            <h2>{category.title}</h2>
            <div className="movies-container">
              <button
                className="scroll-arrow scroll-arrow-left"
                onClick={() => scroll(categoryRefs.current[category.key], "left")}
              >
                &lt;
              </button>
              <div className="movies-grid" ref={categoryRefs.current[category.key]}>
                {category.movies.map((movie) => (
                  <MovieCard
                    key={movie.id}
                    id={movie.id}
                    poster={movie.largePoster || movie.smallPoster}
                    type="movie"
                    featured
                  />
                ))}
              </div>
              <button
                className="scroll-arrow scroll-arrow-left"
                onClick={() => scroll(categoryRefs.current[category.key], "right")}
              >
                &gt;
              </button>
            </div>
          </section>
        );
      })}

      {/* All Movies Section */}
      <section className="all-movies-section">
        <h2>All Movies</h2>
        <div className="movies-container">
          <button
            className="scroll-arrow scroll-arrow-left"
            onClick={() => scroll(allMoviesRef, "left")}
          >
            &lt;
          </button>
          <div className="movies-grid" ref={allMoviesRef}>
            {movies.map((movie) => (
              <MovieCard
                key={movie.id}
                id={movie.id}
                poster={movie.smallPoster}
                type="movie"
              />
            ))}
          </div>
          <button
            className="scroll-arrow scroll-arrow-right"
            onClick={() => scroll(allMoviesRef, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* Movies by Language */}
      {Object.keys(moviesByLanguage).map((language) => {
        if (!languageRefs.current[language]) {
          languageRefs.current[language] = React.createRef();
        }

        return (
          <section key={language} className="language-section">
            <h3>{language} Movies</h3>
            <div className="movies-container">
              <button
                className="scroll-arrow scroll-arrow-left"
                onClick={() => scroll(languageRefs.current[language], "left")}
              >
                &lt;
              </button>
              <div className="movies-grid" ref={languageRefs.current[language]}>
                {moviesByLanguage[language].map((movie) => (
                  <MovieCard
                    key={movie.id}
                    id={movie.id}
                    poster={movie.smallPoster}
                    type="movie"
                  />
                ))}
              </div>
              <button
                className="scroll-arrow scroll-arrow-right"
                onClick={() => scroll(languageRefs.current[language], "right")}
              >
                &gt;
              </button>
            </div>
          </section>
        );
      })}
    </div>
  );
};

export default Movies;
