import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/movies").then((res) => setMovies(res.data));
  }, []);

  return (
    <div className="bg-gray-800 min-h-screen text-white">
      <h2 className="text-3xl font-bold text-center my-6">Featured Movies</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-6 px-8">
        {movies.map((movie) => (
          <div key={movie.id} className="bg-gray-900 p-4 rounded-lg shadow-lg hover:scale-105 transition">
            <img src={movie.poster} alt={movie.title} className="w-full h-56 object-cover rounded-lg" />
            <h3 className="text-lg font-semibold mt-3">{movie.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
