import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import MovieCard from "../components/MovieCard";
import { Link } from "react-router-dom";
import "../styles/Movies.css";

const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [featuredCategories, setFeaturedCategories] = useState([]);
  const [heroCarousels, setHeroCarousels] = useState([]);
  const [indianMovies, setIndianMovies] = useState([]);
  const [featuredIndianMovies, setFeaturedIndianMovies] = useState([]);
  const heroRefs = useRef([]);

  const allMoviesRef = useRef(null);
  const languageRefs = useRef({});
  const categoryRefs = useRef({});
  const indianMoviesRef = useRef(null);
  const featuredIndianRef = useRef(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);

        // Fetch all endpoints simultaneously
        const [
          allMovies,
          featured,
          indian,
          featuredIndian
        ] = await Promise.all([
          axios.get("http://localhost:8080/api/movies"),
          axios.get("http://localhost:8080/api/movies/featured"),
          axios.get("http://localhost:8080/api/movies/indian"),
          axios.get("http://localhost:8080/api/movies/featuredIndian")
        ]);

        setMovies(allMovies.data);
        setIndianMovies(indian.data);
        setFeaturedIndianMovies(featuredIndian.data);

        // Create hero carousels from featured movies
        setHeroCarousels([
          {
            title: "Featured Movies",
            subtitle: "Curated Selection of Premium Content",
            movies: featured.data,
            cta: "Explore All",
            type: "featured"
          },
          {
            title: "Indian Cinema",
            subtitle: "Bollywood & Regional Masterpieces",
            movies: featuredIndian.data,
            cta: "View Indian Films",
            type: "indian"
          }
        ]);

        // Create featured categories
        setFeaturedCategories([
          {
            title: "Trending Now",
            key: "trending",
            movies: featured.data.slice(0, 9)
          },
          {
            title: "Staff Picks",
            key: "staffPicks",
            movies: allMovies.data.sort(() => 0.5 - Math.random()).slice(0, 9)
          }
        ]);

      } catch (err) {
        setError("Failed to fetch movies.");
        console.error("Error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  const scroll = (ref, direction) => {
    if (ref.current) {
      const scrollAmount = direction === "left" ? -800 : 800;
      ref.current.scrollLeft += scrollAmount;
    }
  };

  const groupByLanguage = (items) => {
    return items.reduce((acc, item) => {
      const language = item.language || "Unknown";
      if (!acc[language]) {
        acc[language] = [];
      }
      acc[language].push(item);
      return acc;
    }, {});
  };

  if (loading) return <div className="loading-spinner"></div>;
  if (error) return <p className="error">{error}</p>;

  const moviesByLanguage = groupByLanguage(movies);

  return (
    <div className="movies-page">
      {/* Big Hero Carousels */}
      {heroCarousels.map((carousel, index) => (
        <section key={index} className="hero-carousel">
          <div className="carousel-header">
            <h2>{carousel.title}</h2>
            <p className="carousel-subtitle">{carousel.subtitle}</p>
            <Link 
              to={{
                pathname: "/collection",
                state: { 
                  title: carousel.title,
                  movies: carousel.type === "featured" 
                    ? [...carousel.movies, ...featuredIndianMovies] 
                    : indianMovies
                }
              }} 
              className="cta-button"
            >
              {carousel.cta}
            </Link>
          </div>

          <div className="carousel-container">
            <button 
              className="scroll-arrow left" 
              onClick={() => scroll(heroRefs.current[index], "left")}
            >
              &lt;
            </button>

            <div 
              className="hero-carousel-items" 
              ref={el => heroRefs.current[index] = el}
            >
              {carousel.movies.map(movie => (
                <Link 
                  to={`/movie/${movie.id}`} 
                  key={movie.id} 
                  className="hero-movie-card"
                >
                  <img 
                    src={movie.largePoster} 
                    alt={movie.title}
                    className="hero-poster"
                  />
                  <div className="hero-card-overlay">
                    <h3>{movie.title}</h3>
                    <p>{movie.year}</p>
                  </div>
                </Link>
              ))}
            </div>

            <button 
              className="scroll-arrow right" 
              onClick={() => scroll(heroRefs.current[index], "right")}
            >
              &gt;
            </button>
          </div>
        </section>
      ))}

      {/* Featured Categories */}
      {featuredCategories.map((category) => {
        if (!categoryRefs.current[category.key]) {
          categoryRefs.current[category.key] = React.createRef();
        }

        return (
          <section key={category.key} className="featured-section">
            <h2>{category.title}</h2>
            <div className="movies-container">
              <button
                className="scroll-arrow left"
                onClick={() => scroll(categoryRefs.current[category.key], "left")}
              >
                &lt;
              </button>
              <div className="movies-grid" ref={categoryRefs.current[category.key]}>
                {category.movies.map((movie) => (
                  <MovieCard
                    key={movie.id}
                    id={movie.id}
                    poster={movie.largePoster || movie.smallPoster}
                    type="movie"
                    featured
                  />
                ))}
              </div>
              <button
                className="scroll-arrow right"
                onClick={() => scroll(categoryRefs.current[category.key], "right")}
              >
                &gt;
              </button>
            </div>
          </section>
        );
      })}

      {/* Indian Movies Section */}
      <section className="featured-section">
        <h2>Indian Cinema Collection</h2>
        <div className="movies-container">
          <button
            className="scroll-arrow left"
            onClick={() => scroll(indianMoviesRef, "left")}
          >
            &lt;
          </button>
          <div className="movies-grid" ref={indianMoviesRef}>
            {indianMovies.map(movie => (
              <MovieCard
                key={movie.id}
                id={movie.id}
                poster={movie.smallPoster}
                type="movie"
              />
            ))}
          </div>
          <button
            className="scroll-arrow right"
            onClick={() => scroll(indianMoviesRef, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* Featured Indian Movies Section */}
      <section className="featured-section">
        <h2>Featured Indian Movies</h2>
        <div className="movies-container">
          <button
            className="scroll-arrow left"
            onClick={() => scroll(featuredIndianRef, "left")}
          >
            &lt;
          </button>
          <div className="movies-grid" ref={featuredIndianRef}>
            {featuredIndianMovies.map(movie => (
              <MovieCard
                key={movie.id}
                id={movie.id}
                poster={movie.largePoster || movie.smallPoster}
                type="movie"
                featured
              />
            ))}
          </div>
          <button
            className="scroll-arrow right"
            onClick={() => scroll(featuredIndianRef, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* All Movies Section */}
      <section className="all-movies-section">
        <h2>All Movies</h2>
        <div className="movies-container">
          <button
            className="scroll-arrow left"
            onClick={() => scroll(allMoviesRef, "left")}
          >
            &lt;
          </button>
          <div className="movies-grid" ref={allMoviesRef}>
            {movies.map((movie) => (
              <MovieCard
                key={movie.id}
                id={movie.id}
                poster={movie.smallPoster}
                type="movie"
              />
            ))}
          </div>
          <button
            className="scroll-arrow right"
            onClick={() => scroll(allMoviesRef, "right")}
          >
            &gt;
          </button>
        </div>
      </section>
    </div>
  );
};

export default Movies;
