import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import MovieCard from "../components/MovieCard";
import DarkModeToggle from "../components/DarkModeToggle";
import Hero from "../components/HeroSection";
import ContentSection from "../components/ContentSection";
import CinemaSpotlight from "../components/CinemaSpotlight";
import "../styles/Home.css";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [tvShows, setTvShows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Refs for scroll containers
  const moviesRef = useRef(null);
  const tvShowsRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [moviesRes, tvShowsRes] = await Promise.all([
          axios.get("http://localhost:5004/movies"),
          axios.get("http://localhost:5004/tvShows"),
        ]);
        setMovies(moviesRes.data || []);
        setTvShows(tvShowsRes.data || []);
      } catch (err) {
        setError("Failed to fetch data. Please try again later.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Group movies and TV shows by language
  const groupByLanguage = (items) => {
    return items.reduce((acc, item) => {
      const language = item.language || "Unknown";
      if (!acc[language]) {
        acc[language] = [];
      }
      acc[language].push(item);
      return acc;
    }, {});
  };

  // Get random movies or TV shows by language
  const getRandomMovies = (items, language, count = 5) => {
    const filteredItems = items.filter((item) => item.language === language);
    return filteredItems
      .sort(() => 0.5 - Math.random())
      .slice(0, count);
  };

  // Scroll functionality for horizontal containers
  const scroll = (ref, direction) => {
    if (ref.current) {
      const scrollAmount = direction === "left" ? -500 : 500;
      ref.current.scrollLeft += scrollAmount;
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  const moviesByLanguage = groupByLanguage(movies);
  const tvShowsByLanguage = groupByLanguage(tvShows);

  return (
    <div className="home">
      <DarkModeToggle />

      {/* Hero Section */}
      <Hero />

      {/* Cinema Spotlight */}
      <CinemaSpotlight movies={getRandomMovies(movies, "English", 10)} />

      {/* Content Sections */}
      <ContentSection
        title="Exclusive Hollywood Access"
        description="Get behind-the-scenes access to your favorite movies and TV shows with premium content unavailable anywhere else."
        image="/images/ach.jpg"
      />

      {/* All Movies Section */}
      <section>
        <h3>All Movies</h3>
        <div className="movies-container">
          <button
            className="scroll-arrow scroll-arrow-left"
            onClick={() => scroll(moviesRef, "left")}
          >
            &lt;
          </button>
          <div className="movies-grid" ref={moviesRef}>
            {movies.map((movie) => (
              <MovieCard
                key={movie.id}
                id={movie.id}
                poster={movie.poster}
                type="movie"
              />
            ))}
          </div>
          <button
            className="scroll-arrow scroll-arrow-right"
            onClick={() => scroll(moviesRef, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* All TV Shows Section */}
      <section>
        <h3>All TV Shows</h3>
        <div className="movies-container">
          <button
            className="scroll-arrow scroll-arrow-left"
            onClick={() => scroll(tvShowsRef, "left")}
          >
            &lt;
          </button>
          <div className="movies-grid" ref={tvShowsRef}>
            {tvShows.map((show) => (
              <MovieCard
                key={show.id}
                id={show.id}
                poster={show.poster}
                type="tvshow"
              />
            ))}
          </div>
          <button
            className="scroll-arrow scroll-arrow-right"
            onClick={() => scroll(tvShowsRef, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* Language-Specific Movies Sections */}
      {Object.keys(moviesByLanguage).map((language) => (
        <section key={language}>
          <h3>Top 5 {language} Movies</h3>
          <div className="movies-container">
            <button
              className="scroll-arrow scroll-arrow-left"
              onClick={() => scroll(moviesRef, "left")}
            >
              &lt;
            </button>
            <div className="movies-grid" ref={moviesRef}>
              {getRandomMovies(moviesByLanguage[language], language).map((movie) => (
                <MovieCard
                  key={movie.id}
                  id={movie.id}
                  poster={movie.poster}
                  type="movie"
                />
              ))}
            </div>
            <button
              className="scroll-arrow scroll-arrow-right"
              onClick={() => scroll(moviesRef, "right")}
            >
              &gt;
            </button>
          </div>
        </section>
      ))}

      {/* Language-Specific TV Shows Sections */}
      {Object.keys(tvShowsByLanguage).map((language) => (
        <section key={language}>
          <h3>Top 5 {language} TV Shows</h3>
          <div className="movies-container">
            <button
              className="scroll-arrow scroll-arrow-left"
              onClick={() => scroll(tvShowsRef, "left")}
            >
              &lt;
            </button>
            <div className="movies-grid" ref={tvShowsRef}>
              {getRandomMovies(tvShowsByLanguage[language], language).map((show) => (
                <MovieCard
                  key={show.id}
                  id={show.id}
                  poster={show.poster}
                  type="tvshow"
                />
              ))}
            </div>
            <button
              className="scroll-arrow scroll-arrow-right"
              onClick={() => scroll(tvShowsRef, "right")}
            >
              &gt;
            </button>
          </div>
        </section>
      ))}
    </div>
  );
};

export default Home;
