import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/MovieDetails.css";

const MovieDetails = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5004/movies/${id}`)
      .then((res) => setMovie(res.data))
      .catch(() => {
        axios.get(`http://localhost:5004/tvShows/${id}`)
          .then((res) => setMovie(res.data))
          .catch(() => setError("Movie or TV Show not found!"));
      });
  }, [id]);

  if (error) return <h2 className="error-text">{error}</h2>;
  if (!movie) return <h2 className="loading-text">Loading...</h2>;

  return (
    <div className="movie-details">
      <div className="details-container">
        <div className="poster-container">
          <img src={process.env.PUBLIC_URL + movie.poster} alt={movie.title} />
        </div>
        <div className="info-container">
          <h1>{movie.title}</h1>
          <p className="synopsis">{movie.synopsis}</p>
          <div className="pricing">
            <p className="rent-price">Rent: {movie.rent_price}</p>
            <p className="purchase-price">Buy: {movie.purchase_price}</p>
          </div>
          <div className="buttons">
            <button className="play-btn">â–¶ Play</button>
            <button className="rent-btn">ðŸ’° Rent</button>
            <button className="buy-btn">ðŸ›’ Buy</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetails;
