import React, { useState } from "react";
import Modal from "./Modal";

const plans = [
  { name: "Basic", price: "$5.99/mo" },
  { name: "Standard", price: "$9.99/mo" },
  { name: "Premium", price: "$14.99/mo" },
];

const SubscriptionModal = ({ isOpen, onClose }) => {
  const [selectedPlan, setSelectedPlan] = useState(plans[0]);

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <h2 className="text-2xl font-bold text-center mb-4">Choose a Plan</h2>

      {plans.map((plan) => (
        <div
          key={plan.name}
          className={`p-3 border rounded-md my-2 cursor-pointer ${
            selectedPlan.name === plan.name ? "bg-red-500" : "bg-gray-800"
          }`}
          onClick={() => setSelectedPlan(plan)}
        >
          <h3 className="text-lg">{plan.name} - {plan.price}</h3>
        </div>
      ))}

      <button className="bg-green-500 w-full p-2 mt-4 rounded font-bold">
        Subscribe to {selectedPlan.name}
      </button>
    </Modal>
  );
};

export default SubscriptionModal;
