import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/api';
import '../styles/MovieDetails.css';

const MovieDetails = () => {
    const { type, id } = useParams();
    const [item, setItem] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchItem = async () => {
            try {
                const endpoint = type === 'movie' ? 'movies' : 'tvshows';
                const response = await api[endpoint].getById(id);

                if (!response.data) {
                    throw new Error(`${type === 'movie' ? 'Movie' : 'TV Show'} not found!`);
                }

                setItem(response.data);
            } catch (err) {
                setError(err.response?.data?.message || err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchItem();
    }, [type, id]);

    if (loading) return <div className="loading-spinner"></div>;
    if (error) return <div className="error-message">{error}</div>;
    if (!item) return <div className="not-found">Content not found</div>;

    // Helper function to render list items
    const renderList = (items, fallback = "Not specified") => {
        if (!items || items.length === 0) return fallback;
        return items.join(', ');
    };

    // Helper function to format date
    const formatDate = (dateString) => {
        if (!dateString) return "Unknown";
        return new Date(dateString).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    return (
        <div className="media-details-container">
            {/* Backdrop Image */}
            <div className="backdrop" style={{
                backgroundImage: `linear-gradient(to bottom, rgba(0,0,0,0.2), rgba(0,0,0,0.8)), url(${item.largePoster})`
            }}></div>

            {/* Main Content */}
            <div className="media-content">
                {/* Poster and Basic Info */}
                <div className="media-header">
                    <img 
                        src={item.largePoster || item.smallPoster || '/placeholder.jpg'} 
                        alt={item.title}
                        className="media-poster"
                    />

                    <div className="media-meta">
                        <h1 className="media-title">
                            {item.title}
                            {item.featured && <span className="featured-badge">Featured</span>}
                        </h1>

                        <div className="media-ratings">
                            {item.imdbRating ? (
                                <div className="rating-badge">
                                    <span className="imdb-logo">IMDb</span>
                                    <span>{item.imdbRating}/10</span>
                                </div>
                            ) : null}
                        </div>

                        <div className="media-details">
                            {type === 'movie' ? (
                                <>
                                    {item.year && <span>{item.year}</span>}
                                    {item.runtime && <span>{Math.floor(item.runtime / 60)}h {item.runtime % 60}m</span>}
                                    {item.releaseDate && <span>Released: {formatDate(item.releaseDate)}</span>}
                                </>
                            ) : (
                                <>
                                    {item.startYear && <span>{item.startYear}{item.endYear ? `-${item.endYear}` : ''}</span>}
                                    {item.seasons && <span>{item.seasons} season{item.seasons !== 1 ? 's' : ''}</span>}
                                    {item.episodeRuntime && <span>{item.episodeRuntime}m/episode</span>}
                                </>
                            )}
                        </div>

                        <div className="media-genres">
                            {renderList(item.genres)}
                        </div>
                    </div>
                </div>

                {/* Action Buttons */}
                <div className="media-actions">
                    <button className="play-button">
                        <i className="fas fa-play"></i> Play
                    </button>
                    <button className="action-button">
                        <i className="fas fa-shopping-cart"></i> Buy ${item.purchasePrice || 'N/A'}
                    </button>
                    <button className="action-button">
                        <i className="fas fa-tag"></i> Rent ${item.rentPrice || 'N/A'}
                    </button>
                </div>

                {/* Synopsis */}
                <div className="media-synopsis">
                    <h2>Overview</h2>
                    <p>{item.synopsis || 'No synopsis available.'}</p>
                </div>

                {/* Additional Details */}
                <div className="media-info-grid">
                    <div className="info-section">
                        <h3>Details</h3>
                        <ul>
                            {type === 'movie' && item.budget && (
                                <li><strong>Budget:</strong> ${item.budget.toLocaleString()}</li>
                            )}
                            <li><strong>Country:</strong> {renderList(item.countries)}</li>
                            <li><strong>Language:</strong> {renderList(item.languages)}</li>
                            {type === 'movie' && item.movieCountry && (
                                <li><strong>Industry:</strong> {item.movieCountry}</li>
                            )}
                            <li><strong>Production:</strong> {renderList(item.productionCompanies)}</li>
                            {item.createdAt && (
                                <li><strong>Added:</strong> {formatDate(item.createdAt)}</li>
                            )}
                        </ul>
                    </div>

                    {type === 'tvshow' && (
                        <div className="info-section">
                            <h3>Series Info</h3>
                            <ul>
                                {item.startYear && (
                                    <li><strong>First Aired:</strong> {item.startYear}</li>
                                )}
                                {item.endYear && (
                                    <li><strong>Last Aired:</strong> {item.endYear}</li>
                                )}
                                {item.seasons && (
                                    <li><strong>Seasons:</strong> {item.seasons}</li>
                                )}
                            </ul>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default MovieDetails;
