import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import MovieCard from "../components/MovieCard";
import DarkModeToggle from "../components/DarkModeToggle";
import "../styles/Home.css";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [tvShows, setTvShows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Refs for scroll containers
  const moviesRef = useRef(null);
  const tvShowsRef = useRef(null);
  const genreRefs = useRef({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [moviesRes, tvShowsRes] = await Promise.all([
          axios.get("http://localhost:5004/movies"),
          axios.get("http://localhost:5004/tvShows"),
        ]);
        setMovies(moviesRes.data || []);
        setTvShows(tvShowsRes.data || []);
      } catch (err) {
        setError("Failed to fetch data. Please try again later.");
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const groupByGenre = (items) => {
    return items?.reduce((acc, item) => {
      if (!item.genre) return acc;
      const genres = Array.isArray(item.genre) ? item.genre : [item.genre];
      genres.forEach(genre => {
        acc[genre] = acc[genre] ? [...acc[genre], item] : [item];
      });
      return acc;
    }, {});
  };

  const scroll = (ref, direction) => {
    if (ref.current) {
      const scrollAmount = direction === 'left' ? -500 : 500;
      ref.current.scrollLeft += scrollAmount;
    }
  };

  const moviesByGenre = groupByGenre(movies);
  const tvShowsByGenre = groupByGenre(tvShows);

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="home">
      <DarkModeToggle />
      <h2>Movies & TV Shows</h2>

      {/* Movies Section */}
      <section>
        <h3>Movies</h3>
        <div className="movies-container">
          <button 
            className="scroll-arrow scroll-arrow-left" 
            onClick={() => scroll(moviesRef, 'left')}
          >
            &lt;
          </button>
          <div className="movies-grid" ref={moviesRef}>
            {movies.map((movie) => (
              <MovieCard
                key={movie.id}
                id={movie.id}
                poster={movie.poster}
                type="movie"
              />
            ))}
          </div>
          <button 
            className="scroll-arrow scroll-arrow-right" 
            onClick={() => scroll(moviesRef, 'right')}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* TV Shows Section */}
      <section>
        <h3>TV Shows</h3>
        <div className="movies-container">
          <button 
            className="scroll-arrow scroll-arrow-left" 
            onClick={() => scroll(tvShowsRef, 'left')}
          >
            &lt;
          </button>
          <div className="movies-grid" ref={tvShowsRef}>
            {tvShows.map((show) => (
              <MovieCard
                key={show.id}
                id={show.id}
                poster={show.poster}
                type="tvshow"
              />
            ))}
          </div>
          <button 
            className="scroll-arrow scroll-arrow-right" 
            onClick={() => scroll(tvShowsRef, 'right')}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* Genre Sections */}
      {moviesByGenre && Object.keys(moviesByGenre).map((genre) => (
        <section key={genre}>
          <h3>{genre.toUpperCase()}</h3>
          <div className="movies-container">
            <button 
              className="scroll-arrow scroll-arrow-left" 
              onClick={() => scroll(genreRefs.current[genre], 'left')}
            >
              &lt;
            </button>
            <div 
              className="movies-grid" 
              ref={(el) => (genreRefs.current[genre] = el)}
            >
              {moviesByGenre[genre]?.map((movie) => (
                <MovieCard
                  key={movie.id}
                  id={movie.id}
                  poster={movie.poster}
                  type="movie"
                />
              ))}
              {tvShowsByGenre[genre]?.map((show) => (
                <MovieCard
                  key={show.id}
                  id={show.id}
                  poster={show.poster}
                  type="tvshow"
                />
              ))}
            </div>
            <button 
              className="scroll-arrow scroll-arrow-right" 
              onClick={() => scroll(genreRefs.current[genre], 'right')}
            >
              &gt;
            </button>
          </div>
        </section>
      ))}
    </div>
  );
};

export default Home;
