import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import MovieCard from "../components/MovieCard";
import DarkModeToggle from "../components/DarkModeToggle";
import "../styles/Home.css";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [tvShows, setTvShows] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5004/movies").then((res) => setMovies(res.data));
    axios.get("http://localhost:5004/tvShows").then((res) => setTvShows(res.data));
  }, []);

  const groupByGenre = (items) => {
    return items.reduce((acc, item) => {
      acc[item.genre] = acc[item.genre] ? [...acc[item.genre], item] : [item];
      return acc;
    }, {});
  };

  const moviesByGenre = groupByGenre(movies);
  const tvShowsByGenre = groupByGenre(tvShows);

  // Creating refs for each genre to allow independent scrolling
  const movieScrollRefs = Object.keys(moviesByGenre).reduce((acc, genre) => {
    acc[genre] = useRef(null);
    return acc;
  }, {});

  const tvShowScrollRefs = Object.keys(tvShowsByGenre).reduce((acc, genre) => {
    acc[genre] = useRef(null);
    return acc;
  }, {});

  const scrollContainer = (ref, direction) => {
    if (ref.current) {
      ref.current.scrollBy({ left: direction === "left" ? -300 : 300, behavior: "smooth" });
    }
  };

  return (
    <div className="home">
      <DarkModeToggle />
      <h2>Movies & TV Shows</h2>

      {Object.keys(moviesByGenre).map((genre) => (
        <section key={genre}>
          <h3>{genre.toUpperCase()}</h3>
          <div className="scroll-container">
            <button className="scroll-button left" onClick={() => scrollContainer(movieScrollRefs[genre], "left")}>◀</button>
            <div className="movies-grid" ref={movieScrollRefs[genre]}>
              {moviesByGenre[genre].map((movie) => (
                <MovieCard key={movie.id} id={movie.id} poster={movie.poster} type="movie" />
              ))}
            </div>
            <button className="scroll-button right" onClick={() => scrollContainer(movieScrollRefs[genre], "right")}>▶</button>
          </div>
        </section>
      ))}

      {Object.keys(tvShowsByGenre).map((genre) => (
        <section key={genre}>
          <h3>{genre.toUpperCase()}</h3>
          <div className="scroll-container">
            <button className="scroll-button left" onClick={() => scrollContainer(tvShowScrollRefs[genre], "left")}>◀</button>
            <div className="movies-grid" ref={tvShowScrollRefs[genre]}>
              {tvShowsByGenre[genre].map((show) => (
                <MovieCard key={show.id} id={show.id} poster={show.poster} type="show" />
              ))}
            </div>
            <button className="scroll-button right" onClick={() => scrollContainer(tvShowScrollRefs[genre], "right")}>▶</button>
          </div>
        </section>
      ))}
    </div>
  );
};

export default Home;
