import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api/api"; // Use your centralized API
import "../styles/MovieDetails.css";

const MovieDetails = () => {
  const { type, id } = useParams();
  const [item, setItem] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const endpoint = type === 'movie' ? 'movies' : 'tvshows'; // Ensure correct endpoint
        const response = await api[endpoint].getById(id); // Use structured API call

        if (!response.data) {
          throw new Error(`${type === 'movie' ? 'Movie' : 'TV Show'} not found!`);
        }

        setItem(response.data);
        setError(null);
      } catch (err) {
        setError(err.response?.data?.message || err.message);
        console.error("Error fetching details:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchItem();
  }, [type, id]);

  if (loading) return <h2 className="loading-text">Loading...</h2>;
  if (error) return <h2 className="error-text">{error}</h2>;
  if (!item) return <h2 className="error-text">Item not found</h2>;

  return (
    <div className="movie-details">
      <div className="details-container">
        <div className="poster-container">
          <img 
            src={item.largePoster || item.poster || '/placeholder.jpg'} 
            alt={item.title} 
          />
        </div>
        <div className="info-container">
          <h1>{item.title}</h1>
          <p className="synopsis">{item.synopsis}</p>
          <p className="rating">{item.imdbRating}</p>
          <div className="pricing">
            <p className="rent-price">Rent: {item.rentPrice}</p>
            <p className="purchase-price">Buy: {item.purchasePrice}</p>
          </div>
          <div className="buttons">
            <button className="play-btn">â–¶ Play</button>
            <button className="rent-btn">ðŸ’° Rent</button>
            <button className="buy-btn">ðŸ›’ Buy</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetails;
