import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import MovieCard from "../components/MovieCard";
import { Link } from "react-router-dom";
import "../styles/TVShows.css";

const TVShows = () => {
  const [tvShows, setTvShows] = useState([]);
  const [featuredTvShows, setFeaturedTvShows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const allTvShowsRef = useRef(null);
  const featuredTvShowsRef = useRef(null);
  const languageRefs = useRef({});
  const randomCarousel1Ref = useRef(null);
  const randomCarousel2Ref = useRef(null);
  const randomCarousel3Ref = useRef(null);

  useEffect(() => {
    const fetchTvShows = async () => {
      try {
        setLoading(true);

        const [allShows, featuredShows] = await Promise.all([
          axios.get("http://localhost:8080/api/tvshows"),
          axios.get("http://localhost:8080/api/tvshows/featured")
        ]);

        setTvShows(allShows.data || []);
        setFeaturedTvShows(featuredShows.data || []);
      } catch (err) {
        setError("Failed to fetch TV Shows.");
        console.error("Error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchTvShows();
  }, []);

  const groupByLanguage = (items) => {
    return items.reduce((acc, item) => {
      const language = item.language || "Unknown";
      if (!acc[language]) {
        acc[language] = [];
      }
      acc[language].push(item);
      return acc;
    }, {});
  };

  const getRandomShows = (count = 10) => {
    return [...tvShows]
      .sort(() => 0.5 - Math.random())
      .slice(0, count);
  };

  const scroll = (ref, direction) => {
    if (ref.current) {
      const scrollAmount = direction === "left" ? -800 : 800;
      ref.current.scrollLeft += scrollAmount;
    }
  };

  if (loading) return <div className="loading-spinner"></div>;
  if (error) return <p className="error">{error}</p>;

  const tvShowsByLanguage = groupByLanguage(tvShows);
  const randomShows1 = getRandomShows(10);
  const randomShows2 = getRandomShows(10);
  const randomShows3 = getRandomShows(10);

  return (
    <div className="tvshows-page">
      {/* Featured TV Shows Section */}
      <section className="featured-section">
        <div className="section-header">
          <h2>Featured TV Shows</h2>
          <Link to="/collection" className="see-all">
            See All
          </Link>
        </div>
        <div className="carousel-container">
          <button
            className="scroll-arrow left"
            onClick={() => scroll(featuredTvShowsRef, "left")}
          >
            &lt;
          </button>
          <div className="carousel-items" ref={featuredTvShowsRef}>
            {featuredTvShows.map((show) => (
              <MovieCard
                key={show.id}
                id={show.id}
                poster={show.largePoster || show.smallPoster}
                type="tvshow"
                featured
              />
            ))}
          </div>
          <button
            className="scroll-arrow right"
            onClick={() => scroll(featuredTvShowsRef, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* Random Carousel 1 */}
      <section className="featured-section">
        <div className="section-header">
          <h2>Popular This Week</h2>
          <Link to="/collection" className="see-all">
            See All
          </Link>
        </div>
        <div className="carousel-container">
          <button
            className="scroll-arrow left"
            onClick={() => scroll(randomCarousel1Ref, "left")}
          >
            &lt;
          </button>
          <div className="carousel-items" ref={randomCarousel1Ref}>
            {randomShows1.map((show) => (
              <MovieCard
                key={show.id}
                id={show.id}
                poster={show.smallPoster}
                type="tvshow"
              />
            ))}
          </div>
          <button
            className="scroll-arrow right"
            onClick={() => scroll(randomCarousel1Ref, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* Random Carousel 2 */}
      <section className="featured-section">
        <div className="section-header">
          <h2>Critically Acclaimed</h2>
          <Link to="/collection" className="see-all">
            See All
          </Link>
        </div>
        <div className="carousel-container">
          <button
            className="scroll-arrow left"
            onClick={() => scroll(randomCarousel2Ref, "left")}
          >
            &lt;
          </button>
          <div className="carousel-items" ref={randomCarousel2Ref}>
            {randomShows2.map((show) => (
              <MovieCard
                key={show.id}
                id={show.id}
                poster={show.smallPoster}
                type="tvshow"
              />
            ))}
          </div>
          <button
            className="scroll-arrow right"
            onClick={() => scroll(randomCarousel2Ref, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* Random Carousel 3 */}
      <section className="featured-section">
        <div className="section-header">
          <h2>Staff Picks</h2>
          <Link to="/collection" className="see-all">
            See All
          </Link>
        </div>
        <div className="carousel-container">
          <button
            className="scroll-arrow left"
            onClick={() => scroll(randomCarousel3Ref, "left")}
          >
            &lt;
          </button>
          <div className="carousel-items" ref={randomCarousel3Ref}>
            {randomShows3.map((show) => (
              <MovieCard
                key={show.id}
                id={show.id}
                poster={show.smallPoster}
                type="tvshow"
              />
            ))}
          </div>
          <button
            className="scroll-arrow right"
            onClick={() => scroll(randomCarousel3Ref, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* All TV Shows Section */}
      <section className="featured-section">
        <div className="section-header">
          <h2>All TV Shows</h2>
          <Link to="/collection" className="see-all">
            See All
          </Link>
        </div>
        <div className="carousel-container">
          <button
            className="scroll-arrow left"
            onClick={() => scroll(allTvShowsRef, "left")}
          >
            &lt;
          </button>
          <div className="carousel-items" ref={allTvShowsRef}>
            {tvShows.map((show) => (
              <MovieCard
                key={show.id}
                id={show.id}
                poster={show.smallPoster}
                type="tvshow"
              />
            ))}
          </div>
          <button
            className="scroll-arrow right"
            onClick={() => scroll(allTvShowsRef, "right")}
          >
            &gt;
          </button>
        </div>
      </section>

      {/* TV Shows by Language */}
      {Object.keys(tvShowsByLanguage).map((language) => {
        if (!languageRefs.current[language]) {
          languageRefs.current[language] = React.createRef();
        }

        return (
          <section key={language} className="featured-section">
            <div className="section-header">
              <h2>{language} TV Shows</h2>
              <Link to="/collection" className="see-all">
                See All
              </Link>
            </div>
            <div className="carousel-container">
              <button
                className="scroll-arrow left"
                onClick={() => scroll(languageRefs.current[language], "left")}
              >
                &lt;
              </button>
              <div className="carousel-items" ref={languageRefs.current[language]}>
                {tvShowsByLanguage[language].map((show) => (
                  <MovieCard
                    key={show.id}
                    id={show.id}
                    poster={show.smallPoster}
                    type="tvshow"
                  />
                ))}
              </div>
              <button
                className="scroll-arrow right"
                onClick={() => scroll(languageRefs.current[language], "right")}
              >
                &gt;
              </button>
            </div>
          </section>
        );
      })}
    </div>
  );
};

export default TVShows;
