import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const MovieDetails = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5004/movies/${id}`).then((res) => setMovie(res.data));
  }, [id]);

  if (!movie) return <h2 className="text-center text-white">Loading...</h2>;

  return (
    <div className="bg-gray-800 min-h-screen text-white flex flex-col items-center py-8">
      <h1 className="text-4xl font-bold">{movie.title}</h1>
      <img src={movie.poster} alt={movie.title} className="w-1/2 my-6 rounded-lg shadow-lg" />
      <p className="text-lg">{movie.synopsis}</p>
      <div className="mt-4">
        <p className="text-red-400">Rent: {movie.rent_price}</p>
        <p className="text-green-400">Buy: {movie.purchase_price}</p>
      </div>
    </div>
  );
};

export default MovieDetails;
