import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import MovieCard from "../components/MovieCard";
import "../styles/Movies.css";

const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const moviesRef = useRef(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);
        const res = await axios.get("http://localhost:5004/movies");
        setMovies(res.data || []);
      } catch (err) {
        setError("Failed to fetch movies.");
        console.error("Error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  // Group movies by language
  const groupByLanguage = (items) => {
    return items.reduce((acc, item) => {
      const language = item.language || "Unknown";
      if (!acc[language]) {
        acc[language] = [];
      }
      acc[language].push(item);
      return acc;
    }, {});
  };

  // Scroll functionality
  const scroll = (ref, direction) => {
    if (ref.current) {
      const scrollAmount = direction === "left" ? -500 : 500;
      ref.current.scrollLeft += scrollAmount;
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  const moviesByLanguage = groupByLanguage(movies);

  return (
    <div className="movies-page">
      <h3>All Movies</h3>
      <div className="movies-container">
        <button
          className="scroll-arrow scroll-arrow-left"
          onClick={() => scroll(moviesRef, "left")}
        >
          &lt;
        </button>
        <div className="movies-grid" ref={moviesRef}>
          {movies.map((movie) => (
            <MovieCard
              key={movie.id}
              id={movie.id}
              poster={movie.poster}
              type="movie"
            />
          ))}
        </div>
        <button
          className="scroll-arrow scroll-arrow-right"
          onClick={() => scroll(moviesRef, "right")}
        >
          &gt;
        </button>
      </div>

      {/* Language-Specific Movies Sections */}
      {Object.keys(moviesByLanguage).map((language) => (
        <section key={language}>
          <h3>{language} Movies</h3>
          <div className="movies-container">
            <button
              className="scroll-arrow scroll-arrow-left"
              onClick={() => scroll(moviesRef, "left")}
            >
              &lt;
            </button>
            <div className="movies-grid" ref={moviesRef}>
              {moviesByLanguage[language].map((movie) => (
                <MovieCard
                  key={movie.id}
                  id={movie.id}
                  poster={movie.poster}
                  type="movie"
                />
              ))}
            </div>
            <button
              className="scroll-arrow scroll-arrow-right"
              onClick={() => scroll(moviesRef, "right")}
            >
              &gt;
            </button>
          </div>
        </section>
      ))}
    </div>
  );
};

export default Movies;
