import React, { useEffect, useState } from "react";
import axios from "axios";
import MovieCard from "../components/MovieCard";
import "../styles/Movies.css";

const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);
        const res = await axios.get("http://localhost:5004/movies");
        setMovies(res.data || []);
      } catch (err) {
        setError("Failed to fetch movies.");
        console.error("Error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  // Group movies by language
  const groupByLanguage = (items) => {
    return items.reduce((acc, item) => {
      const language = item.language || "Unknown";
      if (!acc[language]) {
        acc[language] = [];
      }
      acc[language].push(item);
      return acc;
    }, {});
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  const moviesByLanguage = groupByLanguage(movies);

  return (
    <div className="movies-page">
      <h2>All Movies</h2>
      <div className="movies-grid">
        {movies.length > 0 ? (
          movies.map((movie) => (
            <MovieCard
              key={movie.id}
              id={movie.id}
              poster={movie.poster}
              type="movie"
            />
          ))
        ) : (
          <p>No movies available.</p>
        )}
      </div>

      {/* Language-Specific Movies Sections */}
      {Object.keys(moviesByLanguage).map((language) => (
        <section key={language}>
          <h3>{language} Movies</h3>
          <div className="movies-grid">
            {moviesByLanguage[language].map((movie) => (
              <MovieCard
                key={movie.id}
                id={movie.id}
                poster={movie.poster}
                type="movie"
              />
            ))}
          </div>
        </section>
      ))}
    </div>
  );
};

export default Movies;
